package HOJ;
import java.util.*;
public class OJ1089 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner input = new Scanner(System.in);
		int a;
		int b;
		while(input.hasNext())
		{
			a = input.nextInt();
			b = input.nextInt();
			System.out.println(a+b);
		}
		
	}

}
